CREATE TABLE render_job (id BIGINT AUTO_INCREMENT, url VARCHAR(255) NOT NULL, params text, hash VARCHAR(255) NOT NULL UNIQUE, path VARCHAR(255) NOT NULL, accessed_at DATETIME NOT NULL, process_started_at DATETIME, process_finished_at DATETIME, type VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
